import React, { useState } from 'react'
import './AdminLogin.css'
import axios from 'axios'
import { Link, useNavigate } from 'react-router-dom'

function AdminLogin() {
    const navigate = useNavigate();
    const [email, setemail] = useState('')
    const [password, setpassword] = useState('')
    const [error, seterror] = useState({})
       
    let handleLogin=async(e)=>{
        e.preventDefault()
      console.log(email,password);
        try {
          const config = {
            headers: {
              "Content-type": "application/json",
            },
          }

          let logDetails={
            email,
            password
          }
          
          const {data}=await axios.post('/api/admin',logDetails,config);
          console.log("this is out loged in data",data);
          localStorage.setItem("admininfo", JSON.stringify(data))
          if (localStorage.admininfo) {
            // navigate('/admin')
          }
        } catch (error) {
          seterror(error.response.data)
        }
    
      }
  return (
    <div>
    <div className="container">
<div className="row">
  <div style={{paddingTop:"7em"}} className="col-lg-10 col-xl-9 mx-auto ">
    <div  className="card flex-row  border-0 shadow rounded-3 overflow-hidden">
      <div className="card-img-leftt d-none d-md-flex"></div>
      <div className="card-body p-4 p-sm-5">
        <h5 className="card-title text-center mb-5 fw-light fs-3">
          Admin Login
        </h5>
        <form onSubmit={handleLogin} method='post'>
        <label >Email address</label>
          <div className="form-floating mb-3">
         
            <input
              type="email"
              value={email}
              onChange={(e)=>{setemail(e.target.value)}}
            //   className={classname('form-control',{
            //     'is-invalid': error.email
            //     })}
              id="floatingInputEmail"
              placeholder="name@example.com"
              name='email'
              autoFocus
            />
             {/* {error.email && (<div className="invalid-feedback">{error.email}</div>)} */}
          
          </div>

          <label htmlFor="floatingPassword">Password</label>
          <div className="form-floating mb-3">
            <input
              type="password"
              value={password}
              onChange={(e)=>{setpassword(e.target.value)}}
            //   className={classname('form-control',{
            //     'is-invalid': error.password
            //     })}
              id="floatingPassword"
              placeholder="Password"
              name='password'
            />
            {/* {error.password && (<div className="invalid-feedback">{error.password}</div>)} */}
           
          </div>
          <div className="d-grid mb-2">
            <button
              className="btn btn-lg btn-primary btn-login fw-bold text-uppercase"
              type="submit"
            >
              <Link to='/admin' >  Login </Link>
             
            </button>
          </div>
        </form>
      </div>
    </div>
  </div>
</div>
</div>
</div>
  )
}

export default AdminLogin
