import React, { useState } from 'react'
import axios from 'axios'
import { Link } from 'react-router-dom'
import './Register.css'



function Register() {
  const [email, setEmail] = useState("");
  const [name, setName] = useState("");
 
  const [password, setPassword] = useState("");
const [loading, setLoading] = useState(false)
  const [message, setMessage] = useState(null);
  const submitHandler= async (e)=>{
    e.preventDefault()
    console.log(email);
    try {
      const config={
        headers: {
          "Content-type":"application/json",
        }
      }
      setLoading(true)
      const {data}= await axios.post("/api/users",{
        name,email,password
      },config)
      
    } catch (error) {
      
    }
  }
  return (
    <div className="container">
    <div className="row">
      <div style={{paddingTop:"7em"}} className="col-lg-10 col-xl-9 mx-auto ">
        <div  className="card flex-row  border-0 shadow rounded-3 overflow-hidden">
          <div className="card-img-leftt1 d-none d-md-flex"></div>
          <div className="card-body p-4 p-sm-5">
            <h5 className="card-title text-center mb-5 fw-light fs-3">
              Register
            </h5>
            <form onSubmit={submitHandler} method="post">
              <div className="form-floating mb-3">
                <input
                  type="text"
                  value={name}
                  onChange={(e)=>{setName(e.target.value)}}
                  // className={classname('form-control',{
                  // 'is-invalid': error.name
                  // })}
                  id="floatingInputUsername"
                  placeholder="myusername"
                  name="name"
                  autoFocus
                />
                {/* {error.name && (<div className="invalid-feedback">{error.name}</div>)} */}
                {/* <label htmlFor="floatingInputUsername">Username</label> */}
              </div>
              <div className="form-floating mb-3">
                <input
                  type="text"
                  value={email}
                  onChange={(e)=>{setEmail(e.target.value)}}
                  // className={classname('form-control',{
                  //   'is-invalid':error.email
                  // })}
                  id="floatingInputEmail"
                  placeholder="name@example.com"
                  name="email"
                />
                {/* {error.email && (<div className="invalid-feedback">{error.email} </div>)} */}
                {/* <label htmlFor="floatingInputEmail">Email address</label> */}
              </div>

              <div className="form-floating mb-3">
                <input
                  type="password"
                  value={password}
                  onChange={(e)=>{setPassword(e.target.value)}}
                  // className={classname('form-control',{
                  //   'is-invalid':error.password  
                  // })}
                  id="floatingPassword"
                  placeholder="Password"
                  name="password"
                />
                {/* {error.password && (<div className="invalid-feedback">{error.password} </div>)} */}
                {/* <label htmlFor="floatingPassword">Password</label> */}
              </div>
              <div className="d-grid mb-2">
                <button
                  className="btn btn-lg btn-primary btn-login fw-bold text-uppercase"
                  type="submit"
                >
                  Register
                </button>
                <Link className="d-block text-center mt-2 small" to="/">Have an account? Login</Link>
              </div>
            </form>
          </div>
        </div>
      </div>
    </div>
  </div>
  )
}

export default Register
